 val tips = swasthyaAI.getWellnessTips(category)
 _wellnessTips.value = tips
 } catch (e: Exception) {
 _errorMessage.value = "Failed to load wellness tips: ${e.message}"
 } finally {
 _isLoading.value = false
 }
 }
 }

