 data = Uri.parse("tel:100")
 }
 startActivity(intent)
 }

 private fun callAmbulance() {
 val intent = Intent(Intent.ACTION_DIAL).apply {
 data = Uri.parse("tel:108")
 }
 startActivity(intent)
 }

 private fun notifyContacts() {
