 // Check every 100ms for real-time detection
 delay(100)
 } catch (e: Exception) {
 android.util.Log.e(
 "GuardianViewModel",
 "Error in threat monitoring: ${e.message}"
 )
 // Continue monitoring even if one iteration fails
 delay(1000)
 }
 }
