 startAutoRecording()
 alertNearbyUsers()
 }

 GuardianAI.ThreatType.LOUD_NOISE -> {
 // Low priority - monitor and log
 logThreatToBlockchain(threat)
 }

 else -> {
 // No action
 }
 }
 }
 }

